insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.921087','false','2021-12-26 11:30:55.168821','false','<p><strong>(i). KẾT QUẢ LUẬN VĂN SO VỚI NHIỆM VỤ CỦA ĐỀ TÀI ĐẶT RA </strong><i><strong>(Tối đa 50 điểm)</strong></i></p>','0','false',null,null,'21');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.925076','false','2021-12-26 11:30:55.173808','true','<p><strong>Câu 1.</strong><i><strong>Đánh giá về kết quả luận văn so với nhiệm vụ của đề tài đặt ra&nbsp;</strong></i></p><p><i><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></i>A. Kết quả chỉ đáp ứng một phần nhỏ nhiệm vụ của đề tài với khối lượng công việc dưới 50%. <strong>(0≤Điểm&lt;10)</strong></p><p><i><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></i>B. Kết quả đáp ứng phần nhiệm vụ cơ bản của đề tài đặt ra với khối lượng công việc từ 50% đến 70%. <strong>(10≤Điểm&lt;35)</strong></p><p><i><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></i>C. Kết quả đáp ứng phần lớn nhiệm vụ chính của đề tài đặt ra với khối lượng công việc từ 70% đến 95%.&nbsp; <strong>(35≤Điểm&lt;47)</strong></p><p><i><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></i>D. Kết quả đáp ứng đầy đủ nhiệm vụ của đề tài đặt ra với khối lượng công việc trên 95%.&nbsp; <strong>(47≤Điểm≤50)</strong></p>','0','true',null,null,'22');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.92707','false','2021-12-26 11:30:55.170819','false','<p><strong>(ii). VIỆC THỰC HIỆN ĐÁNH GIÁ KẾT QUẢ LUẬN VĂN CỦA SINH VIÊN (Tối đa 20 điểm)</strong></p>','1','false',null,null,'21');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.930063','false','2021-12-26 11:30:55.174805','true','<p><strong>Câu 2.</strong><i><strong>Đánh giá việc nhận diện các lợi ích thực tế của giải pháp trong luận văn</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không nêu được các lợi ích thực tế của giải pháp trong luận văn. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu được một vài lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định nhưng không có minh chứng cụ thể. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định và có kèm theo minh chứng cụ thể. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về các khía cạnh một cách toàn diện và có kèm theo đầy đủ các minh chứng cụ thể.&nbsp;<strong>(4&lt;Điểm≤5)</strong></p>','0','true',null,null,'24');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.932057','false','2021-12-26 11:30:55.175807','true','<p><strong>Câu 3.</strong><i><strong>Đánh giá về những giải pháp được đề xuất trong luận văn để giải quyết vấn đề</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên nêu ra một giải pháp nhưng không biết ưu và nhược điểm. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu ra một giải pháp, và phân tích ưu và nhược điểm. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, có biện luận lựa chọn giải pháp nhưng chưa hợp lý. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, đồng thời nêu rõ lý do và biện luận đúng, rõ ràng tại sao lựa chọn giải pháp đó. <strong>(4&lt;Điểm≤5)</strong></p>','1','true',null,null,'24');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.935049','false','2021-12-26 11:30:55.177799','true','<p><strong>Câu 4.</strong><i><strong>Đánh giá sản phẩm đạt được (mô hình, chương trình, hệ thống, …)&nbsp;</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thực hiện đánh giá sản phẩm đạt được của đề tài. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên trình bày phần đánh giá cho đề tài nhưng chưa đánh giá cho sản phẩm đạt được của đề tài. <strong>(1≤Điểm&lt;5)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên đánh giá sản phẩm đạt được của đề tài, ví dụ: bằng kỹ thuật kiểm tra chuyên môn hoặc làm thí nghiệm, nhưng không phù hợp với các yêu cầu của đề tài. <strong>(5≤Điểm&lt;9)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên đánh giá sản phẩm đạt được của đề tài, ví dụ: bằng kỹ thuật kiểm tra chuyên môn hoặc làm thí nghiệm, phù hợp với các yêu cầu của đề tài. <strong>(9≤Điểm≤10)</strong></p>','2','true',null,null,'24');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.938043','false','2021-12-26 11:30:55.169818','false','<p><strong>(iii). BÀI BÁO CÁO LUẬN VĂN TỐT NGHIỆP (Tối đa 15 điểm)</strong></p>','2','false',null,null,'21');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.941033','false','2021-12-26 11:30:55.179793','true','<p><strong>Câu 5. </strong><i><strong>Khả năng viết báo cáo</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Báo cáo được trình bày không có cấu trúc.<strong> (0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Báo cáo được trình bày có cấu trúc nhưng rời rạc trong việc kết nối các ý tưởng.<strong> (1≤Điểm&lt;5)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Báo cáo được trình bày có cấu trúc, các ý tưởng được kết nối mạch lạc, tuy nhiên chưa thể hiện chi tiết quá trình thực hiện đề tài và kết quả đạt được của đề tài, có nhiều lỗi về hình thức trình bày như chính tả, ngữ pháp, hình ảnh, và bảng biểu. <strong>(5≤Điểm&lt;9)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Báo cáo được trình bày có cấu trúc rõ ràng và có nội dung chi tiết về quá trình thực hiện đề tài và kết quả đạt được của đề tài, các ý tưởng được kết nối mạch lạc, không có (hoặc rất ít) lỗi về hình thức trình bày.<strong> (9≤Điểm≤10)</strong></p>','0','true',null,null,'28');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.944026','false','2021-12-26 11:30:55.180789','true','<p><strong>Câu 6.</strong><i><strong>Việc sử dụng tài liệu tham khảo&nbsp;</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Quá ít tài liệu tham khảo (&lt; 5 tài liệu) được sử dụng trong luận văn. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Tài liệu tham khảo được sử dụng phù hợp trong luận văn, nhưng chưa được trình bày chi tiết và chưa có phần trích dẫn tài liệu. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Tài liệu tham khảo được sử dụng phù hợp trong luận văn, được trình bày chi tiết, nhưng có phần trích dẫn tài liệu chưa đầy đủ. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Tài liệu tham khảo được sử dụng phù hợp trong luận văn, được trình bày chi tiết và có phần trích dẫn tài liệu đầy đủ và rõ ràng. <strong>(4&lt;Điểm≤5)</strong></p>','1','true',null,null,'28');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.956762','false','2021-12-26 11:30:55.172813','true','<p><strong>Điểm thưởng</strong><i>(ghi rõ lý do của điểm thưởng, ví dụ đối với các đề tài có kết quả xuất sắc, có bài báo khoa học và/hoặc đạt các giải thưởng về học thuật) (Tối đa 10 điểm)</i></p>','3','true',null,null,'21');
