insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:06:17.122664','false','2021-12-26 11:39:49.022177','true','<p><strong>Câu 1. </strong><i><strong>Khả năng định nghĩa yêu cầu của vấn đề (bài toán) trong đề tài</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thể xác định các yêu cầu của vấn đề được cho.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên có thể phát biểu và giải thích 1-3 yêu cầu của vấn đề được cho, nhưng thiếu ràng buộc, chưa rõ ràng, không thể kiểm tra được, hay phụ thuộc hiện thực.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên có thể phát biểu và giải thích tất cả các yêu cầu của vấn đề được cho; trong đó, một số yêu cầu có thể kiểm tra được và một số yêu cầu thiếu ràng buộc hoặc phụ thuộc hiện thực.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên có thể phát biểu và giải thích tất cả các yêu cầu của vấn đề được cho với các ràng buộc rõ ràng, có thể kiểm tra được, và không phụ thuộc hiện thực.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; E. Không đánh giá được vì:&nbsp;</p>','0','true',null,null,'32');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:06:17.126653','false','2021-12-26 11:39:49.023174','true','<p><strong>Câu 2. </strong><i><strong>Khả năng phân tích vấn đề cho bài toán trong đề tài</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thể xác định các thành phần chính của vấn đề.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên có thể nhận diện một số thành phần chính nhưng chưa đúng hoặc không thể kiểm tra liệu có phù hợp cho vấn đề.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên có thể nhận diện một số thành phần chính và thể hiện được sự liên hệ giữa các thành phần của vấn đề.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên có thể nhận diện tất cả thành phần và các mối liên hệ giữa các thành phần này cho vấn đề, tạo cơ sở để hình thành giải pháp cho vấn đề.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; E. Không đánh giá được vì:&nbsp;</p>','1','true',null,null,'32');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:06:17.129645','false','2021-12-26 11:39:49.025179','true','<p><strong>Câu 3.</strong><i><strong> Khả năng thiết kế hệ thống (một phần hệ thống như kiến trúc phần mềm, cơ sở dữ liệu, mạng, …) dựa trên yêu cầu bài toán</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thể thiết kế hệ thống để đáp ứng yêu cầu bài toán.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên có thể thiết kế hệ thống nhưng cần nhiều hướng dẫn chi tiết để đáp ứng yêu cầu bài toán.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên có thể thiết kế hệ thống nhưng vẫn cần hướng dẫn để đáp ứng yêu cầu bài toán.&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên có thể tự thiết kế hệ thống phù hợp để đáp ứng yêu cầu bài toán.&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; E. Không đánh giá được vì:&nbsp;</p>','2','true',null,null,'32');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:06:17.133635','false','2021-12-26 11:39:49.027179','true','<p><strong>Câu 4.</strong><i><strong> Khả năng sử dụng các nguyên lý phát triển phần mềm, các quy trình phát triển phần mềm, và/ hoặc các công cụ thiết kế phần mềm, … để mô hình hóa hệ thống phần mềm trong đề tài luận văn</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thể áp dụng các nguyên lý phát triển phần mềm, các quy trình phát triển phần mềm, và/ hoặc các công cụ thiết kế phần mềm, …để mô hình hóa hệ thống phần mềm trong đề tài luận văn.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên có thể áp dụng các nguyên lý phát triển phần mềm, các quy trình phát triển phần mềm, và/ hoặc các công cụ thiết kế phần mềm, …; nhưng giới hạn cho 1-2 khía cạnh (<i>view</i>) của hệ thống phần mềm như dữ liệu và/ hoặc chức năng&nbsp;của hệ thống phần mềm. Phần mô hình hóa chưa liên hệ được với các yêu cầu của hệ thống phần mềm.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên có thể áp dụng các nguyên lý phát triển phần mềm, các quy trình phát triển phần mềm, và/ hoặc các công cụ thiết kế phần mềm, …; nhưng giới hạn cho 3-4 khía cạnh (<i>view</i>) của hệ thống phần mềm. Phần mô hình hóa liên hệ được với một số yêu cầu của hệ thống phần mềm.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên có thể áp dụng các nguyên lý phát triển phần mềm, các quy trình phát triển phần mềm, và/ hoặc các công cụ thiết kế phần mềm, … để mô hình hóa đầy đủ hệ thống phần mềm và liên hệ được đầy đủ các yêu cầu của hệ thống phần mềm.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; E. Không đánh giá được vì:&nbsp;</p>','3','true',null,null,'32');
