insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:09:28.935701','false','2021-12-26 11:32:12.561664','false','<p><strong>(i). KẾT QUẢ LUẬN VĂN SO VỚI NHIỆM VỤ CỦA ĐỀ TÀI ĐẶT RA (tối đa 50 điểm)</strong></p>','0','false',null,null,'37');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:09:28.939691','false','2021-12-26 11:32:12.564654','true','<p><strong>Câu 1.</strong> <i><strong>Đánh giá về kết quả luận văn so với nhiệm vụ của đề tài đặt ra&nbsp;</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Kết quả chỉ đáp ứng một phần nhỏ nhiệm vụ của đề tài với khối lượng công việc dưới 50%. <strong>(0≤Điểm&lt;10)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Kết quả đáp ứng phần nhiệm vụ cơ bản của đề tài đặt ra với khối lượng công việc từ 50% đến 70%. <strong>(10≤Điểm&lt;35)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Kết quả đáp ứng phần lớn nhiệm vụ chính của đề tài đặt ra với khối lượng công việc từ 70% đến 95%.&nbsp; <strong>(35≤Điểm&lt;47)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Kết quả đáp ứng đầy đủ nhiệm vụ của đề tài đặt ra với khối lượng công việc trên 95%.&nbsp; <strong>(47≤Điểm≤50)</strong></p>','0','true',null,null,'38');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.930063','false','2021-12-26 11:32:12.566243','true','<p><strong>Câu 2.</strong><i><strong>Đánh giá việc nhận diện các lợi ích thực tế của giải pháp trong luận văn</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không nêu được các lợi ích thực tế của giải pháp trong luận văn. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu được một vài lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định nhưng không có minh chứng cụ thể. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định và có kèm theo minh chứng cụ thể. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về các khía cạnh một cách toàn diện và có kèm theo đầy đủ các minh chứng cụ thể.&nbsp;<strong>(4&lt;Điểm≤5)</strong></p>','1','true',null,null,'38');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 16:42:57.932057','false','2021-12-26 11:32:12.56266','true','<p><strong>Câu 3.</strong><i><strong>Đánh giá về những giải pháp được đề xuất trong luận văn để giải quyết vấn đề</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên nêu ra một giải pháp nhưng không biết ưu và nhược điểm. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu ra một giải pháp, và phân tích ưu và nhược điểm. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, có biện luận lựa chọn giải pháp nhưng chưa hợp lý. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, đồng thời nêu rõ lý do và biện luận đúng, rõ ràng tại sao lựa chọn giải pháp đó. <strong>(4&lt;Điểm≤5)</strong></p>','2','true',null,null,'38');
