insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:13:40.607925','false','2021-12-26 11:33:11.183149','false','<p><strong>I. ĐÁNH GIÁ KẾT QUẢ LUẬN VĂN (tối đa 50 điểm)</strong></p>','0','false',null,null,'47');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:13:40.610882','false','2021-12-26 11:33:11.184147','true','<p><strong>Câu 1.</strong> <i><strong>Đánh giá kết quả luận văn so với nhiệm vụ của đề tài đặt ra&nbsp;</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Kết quả chỉ đáp ứng một phần nhỏ nhiệm vụ của đề tài với khối lượng công việc dưới 50%. <strong>(0≤Điểm&lt;10)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Kết quả đáp ứng phần nhiệm vụ cơ bản của đề tài đặt ra với khối lượng công việc từ 50% đến 70%. <strong>(10≤Điểm&lt;35)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Kết quả đáp ứng phần lớn nhiệm vụ chính của đề tài đặt ra với khối lượng công việc từ 70% đến 95%.&nbsp; <strong>(35≤Điểm&lt;47)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Kết quả đáp ứng đầy đủ nhiệm vụ của đề tài đặt ra với khối lượng công việc trên 95%.&nbsp; <strong>(47≤Điểm≤50)</strong></p>','0','true',null,null,'48');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:13:40.613878','false','2021-12-26 11:33:11.18614','true','<p><strong>Câu 2.</strong> <i><strong>Đánh giá việc nhận diện các lợi ích thực tế của giải pháp trong luận văn</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không nêu được các lợi ích thực tế của giải pháp trong luận văn. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu được một vài lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định nhưng không có minh chứng cụ thể. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về một vài khía cạnh nhất định và có kèm theo minh chứng cụ thể. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên nêu được các lợi ích thực tế của giải pháp trong luận văn về các khía cạnh một cách toàn diện và có kèm theo đầy đủ các minh chứng cụ thể.&nbsp;<strong>(4&lt;Điểm≤5)</strong></p>','1','true',null,null,'48');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:13:40.617864','false','2021-12-26 11:33:11.188138','true','<p><strong>Câu 3.</strong> <i><strong>Đánh giá về những giải pháp được đề xuất trong luận văn để giải quyết vấn đề</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên nêu ra một giải pháp nhưng không biết ưu và nhược điểm. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên nêu ra một giải pháp, và phân tích ưu và nhược điểm. <strong>(1≤Điểm&lt;3)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, có biện luận lựa chọn giải pháp nhưng chưa hợp lý. <strong>(3≤Điểm≤4)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên đưa ra nhiều giải pháp để giải quyết bài toán, đồng thời nêu rõ lý do và biện luận đúng, rõ ràng tại sao lựa chọn giải pháp đó.&nbsp;<strong>(4&lt;Điểm≤5)</strong></p>','2','true',null,null,'48');
insert into sc_criterion(created_at,deleted,updated_at,comment,description,display_order,mark,max_score,min_score,parent_id) values('2021-11-20 17:13:40.620855','false','2021-12-26 11:33:11.190129','true','<p><strong>Câu 4.</strong> <i><strong>Đánh giá sản phẩm đạt được của đề tài (mô hình, chương trình, hệ thống, …)&nbsp;</strong></i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; A. Sinh viên không thực hiện đánh giá sản phẩm đạt được của đề tài. <strong>(0≤Điểm&lt;1)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; B. Sinh viên trình bày phần đánh giá cho đề tài nhưng chưa đánh giá cho sản phẩm đạt được của đề tài. <strong>(1≤Điểm&lt;5)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; C. Sinh viên đánh giá sản phẩm đạt được của đề tài, ví dụ: bằng kỹ thuật kiểm tra chuyên môn hoặc làm thí nghiệm, nhưng không phù hợp với các yêu cầu của đề tài. <strong>(5≤Điểm&lt;9)</strong></p><p>&nbsp; &nbsp; &nbsp; &nbsp; D. Sinh viên đánh giá sản phẩm đạt được của đề tài, ví dụ: bằng kỹ thuật kiểm tra chuyên môn hoặc làm thí nghiệm, phù hợp với các yêu cầu của đề tài. <strong>(9≤Điểm≤10)</strong></p>','3','true',null,null,'48');
